    @include vintage-border;
    width: 100%;
    max-height: 300px;
    object-fit: cover;
    margin-bottom: 20px;
  }
  
  h2 {
    color: $primary-color;
    font-family: 'Playfair Display', serif;
    font-size: 24px;
    margin-bottom: 16px;
  }
  
  ion-segment {
    margin-top: 24px;
    margin-bottom: 24px;
    --background: #{$card-background};
    
    ion-segment-button {
      --color: #{$text-color};
      --color-checked: #{$light-text-color};
      --background-checked: #{$accent-color};
      font-family: 'Georgia', serif;
    }
  }
  
  p {
    color: $text-color;
    font-size: 16px;
    line-height: 1.6;
    margin-bottom: 16px;
  }

  h3 {
    color: $primary-color;
    font-family: 'Playfair Display', serif;
    font-size: 20px;
    margin-top: 24px;
    margin-bottom: 16px;
  }

  ol {
    padding-left: 24px;
    color: $text-color;

    li {
      margin-bottom: 8px;
    }
  }
  
  ion-text {
    h3 {
      color: $accent-color;
      font-size: 22px;
      font-weight: bold;
      margin-top: 24px;
    }

    p {
      color: $text-color;
      font-style: italic;
    }
  }
  
  ion-button {
    margin-top: 24px;
    --background: #{$accent-color};
    --color: #{$light-text-color};
    --border-radius: 0;
    @include vintage-border;
    font-family: 'Georgia', serif;

    &[disabled] {
      opacity: 0.5;
    }
  }
}

