      }
    }

    ion-item-options {
      ion-item-option {
        &[color="primary"] {
          --ion-color-base: #{$accent-color};
        }
        &[color="danger"] {
          --ion-color-base: #B22222; // FireBrick
        }
      }
    }
  }
}

